//Informations Demandeur 
 const listErrors = [];  
 function isEmpty(value) { 
   return ( 
     value == null || 
     value === '' || 
     (Array.isArray(value) && value.length === 0) 
   ); 
 } 


 //prospect 
 if (data['id'] === 'Client' && data['prospect'] && data['prospect'].includes('Oui')) { 
     listErrors.push(utils.error('prospect', `ce client existe deja il ne peut etre un prospect.`)); 
 } 
 // this shows for few min then fade   
 // if ( data['codeClient'] && !data['id'] && (isEmpty(data['prospect']) || !data['prospect']) ) { listErrors.push(utils.error('prospect', `Ce champ est obligatoire.`)); } 

 // if prospect : 
 // morale : raison sociale , denomination sociale, telephone ou email  
 // physique : nom , prenom , telephone ou email 
 let nonClient = data['nonClient'] || {}; 

 if (data['id'] !== 'Client' ) { 
     //required 
     if(data['segment'] === 'Personne Morale') { 
         if ( isEmpty(nonClient['raisonSociale']) ) { listErrors.push(utils.error('nonClient.raisonSociale', `Ce champ est obligatoire.`)); } 
         if ( isEmpty(nonClient['denominationSociale']) ) { listErrors.push(utils.error('nonClient.denominationSociale', `Ce champ est obligatoire.`)); } 
     } else { 
         if ( isEmpty(nonClient['nom']) ) { listErrors.push(utils.error('nonClient.nom', `Ce champ est obligatoire.`)); } 
         if ( isEmpty(nonClient['prenom']) ) { listErrors.push(utils.error('nonClient.prenom', `Ce champ est obligatoire.`)); } 
     } 
     if ( isEmpty(nonClient['telephone']) && isEmpty(nonClient['email']) ) { listErrors.push(utils.error('nonClient.telephone', `Ce champ est obligatoire.`)); listErrors.push(utils.error('nonClient.email', `Ce champ est obligatoire.`));} 

     // regEx 
     // phone 
     if (!isEmpty(nonClient['telephone']) && !/^0\d{8,9}$/.test(nonClient['telephone']) ){ listErrors.push(utils.error('nonClient.telephone','Ce champ doit être un numéro de téléphone valide.')); } 
     // email 
     if (!isEmpty(nonClient['email']) && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(nonClient['email']) ){ listErrors.push(utils.error('nonClient.email','Ce champ doit être une adresse email valide.')); } 
     // numerique 
     const numFields = ['nif','stat','nCNI','nCompte','IBAN','BP']; 
     numFields.forEach((field) => { 
       if (!isEmpty(nonClient[field]) && !/^[0-9 ]+$/.test(nonClient[field]) ){  
         listErrors.push(utils.error(`nonClient.${field}`,'Ce champ doit contenir uniquement des chiffres et des espaces.')); 
       } 
     }); 
     // dates 
     const dateFieldsProspect = ['dateCreation','dateNaissance','dateDelivranceCIN']; 
     dateFieldsProspect.forEach((field) => { 
         if (!isEmpty(nonClient[field])) { 
             const inputDate = new Date(nonClient[field]); 
             const today = new Date(); 
             today.setHours(0,0,0,0); // Ignore time part 
             if (inputDate <= today) { 
                 listErrors.push(utils.error(`nonClient.${field}`, String.raw`La date doit être postérieure à aujourd’hui.`)); 
             } 
         } 
     }); 
 } 


 return listErrors;

