function calculeToT_VisiteCaution(context) { 
     let patrimoines = (context.patrimoines || []).map(item => { 
         let totalValeurBrut = 
             (item.foncierHorsBilan || 0) + 
             (item.residencePrincipale || 0) + 
             (item.residenceSecondaire || 0) + 
             (item.residenceLocative || 0) + 
             (item.epargneStable || 0) + 
             (item.caisse || 0) + 
             (item.pretAvancesClientele || 0) + 
             (item.autres || 0); 

         return { 
             ...item, 
             totalValeurBrut: totalValeurBrut 
         }; 
     }); 

     let engagements = (context.engagements || []).map(item => { 
         let totalEncoursLies = 
             (item.foncierHorsBilanEngagements || 0) + 
             (item.habitat || 0) + 
             (item.creditFDR || 0) + 
             (item.encoursConcurrence || 0); 

         return { 
             ...item, 
             totalEncoursLies: totalEncoursLies 
         }; 
     }); 

     return { 
         patrimoines: patrimoines, 
         engagements: engagements 
     }; 
 } 

 return calculeToT_VisiteCaution(context);

